import React, {useState} from 'react'

function Calculator() {
    const [firstNum , setFirstNum ] = useState('');
    const [firstSet , setFirstSet ] = useState(false);
    const [secondNum, setSecondNum ] = useState('');
    const [result, setResult ] = useState();
    const [ oprtr, setOprtr] = useState('');

    let handleNumClick = (val) => {
        if(firstSet){
            setFirstNum(firstNum+val);
        }
        else{
            setSecondNum(secondNum+val);
        }
    }

    let handleOperatorClick = (val) => {
        setOprtr(val);
        setFirstSet(true);
    }

    let handleResultClick = () => {
        if(oprtr === 'add'){
            setResult(parseFloat(firstNum) + parseFloat(secondNum));
        }
        else if(oprtr === 'substract'){
            setResult(parseFloat(firstNum) - parseFloat(secondNum));
        }
        else if(oprtr === 'multiply'){
            setResult(parseFloat(firstNum) * parseFloat(secondNum));
        }
        else if(oprtr === 'divide'){
            setResult(parseFloat(firstNum) / parseFloat(secondNum));
        }
        else if(oprtr === 'module'){
            setResult(parseFloat(firstNum) % parseFloat(secondNum));
        }

        setFirstSet(false);
        setFirstNum(result);
    }
    let handleAC = () => {
        setFirstNum('');
        setSecondNum('');
        setFirstSet(false);
    }
  return (
    <div>
        <div>
            {firstSet ? (
                <div>
                    <h3>{firstNum}</h3>
                    <h2>{secondNum}</h2>
                </div>
            ) : (
                <div>
                    <h2>{firstNum}</h2>
                </div>
            )}
        </div>
        <div>
            <button onClick={handleAC}>AC</button>
            <button value="module" onClick={(e) => (handleOperatorClick(e.target.value))}>%</button>
            <button value="divide" onClick={(e) => (handleOperatorClick(e.target.value))}>/</button>
        </div>
        <div>
            <button>7</button>
            <button>8</button>
            <button>9</button>
            <button value="multiply" onClick={(e) => (handleOperatorClick(e.target.value))}>x</button>
        </div>
        <div>
            <button>4</button>
            <button>5</button>
            <button>6</button>
            <button value="substract" onClick={(e) => (handleOperatorClick(e.target.value))}>-</button>
        </div>
        <div>
            <button>1</button>
            <button>2</button>
            <button>3</button>
            <button value="add" onClick={(e) => (handleOperatorClick(e.target.value))}>+</button>
        </div>
        <div>
            <button>0</button>
            <button>,</button>
            <button onClick={handleResultClick}>=</button>
        </div>
    </div>
  )
}

export default Calculator
